#include "../webs.h"

int myFunc0(webs_client* self) {
  printf("server %ld: (id %ld) connected!\n", self->srv->id, self->id);
  webs_send(self, "gotya8");
  webs_send(self, "20Kb:");
  
  return 0;
}

int myFuncZ(webs_client* self) {
  printf("server %ld: (id %ld) connected!\n", self->srv->id, self->id);
  webs_send(self, "greetings, salutations!");
  
  return 0;
}

int myFunc1(webs_client* self, char* data, ssize_t len) {
  if (len < 16384)
  printf("server %ld: (id %ld) data [ %s ] (%lu bytes)\n", self->srv->id, self->id, data, len);
  
  else
  printf("server %ld: (id %ld) data {TOO MUCH, I WONT RUIN YOUR NICE TERMINAL BUT REST ASSURED WE HAVE DATA} (%lu bytes)\n", self->srv->id, self->id, len);
  
  if (data[0] == 'E') webs_eject(self);
  if (data[0] == 'C') webs_close(self->srv);
  
  return 0;
}

int myFunc2(webs_client* self) {
  printf("server %ld: (id %ld) disconnected!\n", self->srv->id, self->id);
  
  return 0;
}

int myFunc3(webs_client* self, enum webs_error err) {
  
  switch (err) {
    case WEBS_ERR_NONE:
      printf("server %ld - on_error: OK\n", self->srv->id);
      break;
    case WEBS_ERR_READ_FAILED:
      printf("server %ld - on_error: read failed.\n", self->srv->id);
      break;
    case WEBS_ERR_NO_SUPPORT:
      printf("server %ld - on_error: frame opcode unsupported.\n", self->srv->id);
      break;
    case WEBS_ERR_OVERFLOW:
      printf("server %ld - on_error: recieved too much data (more than SSIZE_MAX).\n", self->srv->id);
      break;
    case WEBS_ERR_UNEXPECTED_CONTINUTATION:
      printf("server %ld - on_error: recieved unexpected continuation frame.\n", self->srv->id);
      break;
  }
  
  return 0;
}

int myFunc4(webs_client * self, const char * path) {
  (void)self;
  return (strcmp(path, "/polling") == 0);
}

int main(void) {
  webs_server* server0 = webs_start(7752, 0);
  webs_server* server1 = webs_start(7754, 1);
  
  if (!server0 || !server1) {
    printf("failed to initialise a server.\n");
    return 1;
  }
  
  server0->events.on_open = myFunc0;
  server0->events.on_data = myFunc1;
  server0->events.on_close = myFunc2;
  server0->events.on_error = myFunc3;
  server0->events.is_route = myFunc4;
  
  /* not all handlers need to be set... */
  server1->events.on_open = myFuncZ;
  server1->events.on_close = myFunc2;
  server1->events.on_data = myFunc1;
  
  webs_hold(server0);
  webs_hold(server1);
  
  webs_close(server0);
  webs_close(server1);
  
  return 0;
}
